import { GET_ALL_DOGS, GET_ALL_TEMPERAMENTS, GET_DOGS_BY_NAME, ADD_DOG, FILTER_BY_ORIGIN, ORDER_BY_NAME } from "./actions";

const initialState = {
    allDogs: [],
    dogShown: [],
    temperaments: []
}

const rootReducer = (state = initialState, action) => {
    switch (action.type) {
        case GET_ALL_DOGS:
            return {
                ...state,
                dogShown: [...action.payload],
                allDogs: [...state.allDogs, ...action.payload]
            }

        case GET_ALL_TEMPERAMENTS:
            return {
                ...state,
                temperaments: [...action.payload]
            }

        case GET_DOGS_BY_NAME:
            return {
                ...state,
                dogShown: [...action.payload]
            }

        case ADD_DOG:
            return {
                ...state,
                dogShown: [...state.dogShown, action.payload],
                allDogs: [...state.allDogs, action.payload]
            }

        case FILTER_BY_ORIGIN:
            let filteredByOrigin = []
            if (action.payload === 'Api') filteredByOrigin = state.allDogs.filter(dog => typeof dog.id === 'number')
            if (action.payload === 'User') filteredByOrigin = state.allDogs.filter(dog => typeof dog.id === 'string')
            if (action.payload === 'selectOrigin') filteredByOrigin = state.allDogs
            return {
                ...state,
                dogShown: filteredByOrigin
            }

        case ORDER_BY_NAME:
            return {
                ...state,
                dogShown:
                    action.payload === 'ascName'
                        ? state.allDogs.sort((a, b) => a.name.localeCompare(b.name))
                        : state.allDogs.sort((a, b) => b.name.localeCompare(a.name))
            }

        default: return {
            ...state
        };


    }
};

export default rootReducer;
