import './Form.css';
import { React, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import * as actions from '../../redux/actions';

export default function Form(props) {
    const dispatch = useDispatch();

    const temperaments = useSelector(store => store.temperaments)

    const { input, setInput, handleInputChange, setErrors, handleSubmit } = props;

    // const [input, setInput] = useState({
    //     name: '',
    //     image: '',
    //     temperaments: '',
    //     span: '',
    //     maxHeight: '',
    //     minHeight: '',
    //     maxWeight: '',
    //     minWeight: '',
    // });


    return (
        <div className="formDiv">
            <form onSubmit={handleSubmit}>
                <label>Name: </label>
                <input
                    className='inputGral'
                    name='name'
                    type="text"
                    placeholder='Name of the dog breed...'
                    onChange={handleInputChange}
                    value={input.name} />
                <hr />
                <label>Image: </label>
                <input
                    className='inputGral'
                    name='image'
                    placeholder='Copy the url of a cool image of the dog...'
                    type='text'
                    onChange={handleInputChange}
                    value={input.image} />
                <hr />
                <label>Life span: </label>
                <input
                    className='inputGral'
                    name='span'
                    type="text"
                    placeholder='How many years do this breed live...'
                    onChange={handleInputChange}
                    value={input.span} />
                <label> years</label>
                <hr />
                <label>Height: </label>
                <input
                    className='inputGral'
                    name='minHeight'
                    type="number"
                    placeholder='Min height'
                    onChange={handleInputChange}
                    value={input.minHeight} />
                <label> - </label>
                <input
                    className='inputGral'
                    name='maxHeight'
                    type="number"
                    placeholder='Max height'
                    onChange={handleInputChange}
                    value={input.maxHeight} />
                <label> cm.</label>
                <hr />
                <label>Weight: </label>
                <input
                    className='inputGral'
                    name='minWeight'
                    type="number"
                    placeholder='Min weight'
                    onChange={handleInputChange}
                    value={input.minWeight} />
                <label> - </label>
                <input
                    className='inputGral'
                    name='maxWeight'
                    type="number"
                    placeholder='Max weight'
                    onChange={handleInputChange}
                    value={input.maxWeight} />
                <label> kg.</label>
                <hr />
                <label> Temperament: </label>
                <div className="tempDiv">
                    {
                        temperaments.map((temp) => {
                            return <p>{temp.name} <input className='tempCheck'
                                name='temps'
                                id={temp.id}
                                type='checkbox'
                                // onChange={handleTemps}
                                value={temp.name} /></p>
                        })
                    }
                </div>
            </form>
        </div>
    )
}
