import axios from "axios";

export const GET_ALL_DOGS = 'GET_ALL_DOGS'
export const GET_ALL_TEMPERAMENTS = 'GET_ALL_TEMPERAMENTS'
export const GET_DOGS_BY_NAME = 'GET_DOGS_BY_NAME'
export const ADD_DOG = 'ADD_DOG'
export const FILTER_BY_TEMPERAMENT = 'FILTER_BY_TEMPERAMENT'
export const FILTER_BY_ORIGIN = 'FILTER_BY_ORIGIN'
export const ORDER_BY_NAME = 'ORDER_BY_NAME'
export const ORDER_BY_WEIGHT = 'ORDER_BY_WEIGHT'

export const getAllDogs = () => {
    return async function (dispatch) {
        let response = await axios.get('http://localhost:3001/dogs')
        return dispatch(
            {
                type: GET_ALL_DOGS,
                payload: response.data
            }
        )
    }
}

export const getAllTemperaments = () => {
    return async function (dispatch) {
        let response = await axios.get('http://localhost:3001/temperaments')
        return dispatch(
            {
                type: GET_ALL_TEMPERAMENTS,
                payload: response.data
            }
        )
    }
}

export const getDogsByName = (dog) => {
    return async function (dispatch) {
        let response = await axios.get(`http://localhost:3001/dogs/name?name=${dog}`)
        return dispatch(
            {
                type: GET_DOGS_BY_NAME,
                payload: response.data
            }
        )
    }
}

export const addDog = (dog) => {
    console.log('esto recibe la fción addDog', dog)

    let perro = {
        name: dog.name,
        image: dog.image,
        span: dog.span + 'years',
        height: dog.minHeight + ' - ' + dog.maxHeight,
        weight: dog.minWeight + ' - ' + dog.maxWeight,
        temperament: dog.temperaments.join(', ')
    }

    console.log('perro en addDog after treatment', perro)
    return async function (dispatch) {
        await axios.post('http://localhost:3001/dogs/newDog', { dog }) // se envía así porque es un post y envía un JSON!!!
            .then((response) => {
                console.log('la response en addDog', response)
                return dispatch({
                    type: ADD_DOG,
                    payload: response
                })
            })
            .catch((err) => console.log(err))
    }
}

export const filterByTemperament = (temperament) => {
    return {
        type: FILTER_BY_TEMPERAMENT,
        payload: temperament,
    }
}

export const filterByOrigin = (origin) => {
    return {
        type: FILTER_BY_ORIGIN,
        payload: origin
    }
}

export const orderByName = (order) => {
    return {
        type: ORDER_BY_NAME,
        payload: order
    }
}

export const orderByWeight = (order) => {
    return {
        type: ORDER_BY_WEIGHT,
        payload: order
    }
}
